import React from 'react';
import { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes ,Navigate} from 'react-router-dom';

import Login from './pages/Login';         // 로그인 페이지
import Signup from './pages/Signup';       // 회원가입 페이지
import Dashboard from './components/Dashboard'; // 대시보드 페이지
import Camera from './components/Camera';
import Home from './pages/Home';
import Navbar from './components/Navbar';

const App = () => {

  const [isLoggedIn, setIsLoggedIn] = useState(false);
   // 로그인 상태 확인
  useEffect(() => {
    const token = localStorage.getItem('access_token');
    setIsLoggedIn(!!token);
  }, []);

  return (
    <Router>
    {isLoggedIn && <Navbar setIsLoggedIn={setIsLoggedIn} />}  {/* 로그인 상태일 때만 Navbar 보이게 */}
    <Routes>
        <Route path="/home" element={<Home setIsLoggedIn={setIsLoggedIn}/>} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
        <Route path="/dashboard" element={isLoggedIn ? <Dashboard /> : <Navigate to="/home" />} />
        <Route path="/camera" element={isLoggedIn ? <Camera /> : <Navigate to="/home" />} />
        <Route path="*" element={<Navigate to="/home" />} />
    </Routes>
  </Router>
);
};

export default App;
