import React from 'react';
import { BrowserRouter as Router, Route, Routes ,Navigate} from 'react-router-dom';

import Login from './pages/Login';         // 로그인 페이지
import Signup from './pages/Signup';       // 회원가입 페이지
import Dashboard from './components/Dashboard'; // 대시보드 페이지
import Camera from './components/Camera';

import Navbar from './components/Navbar';

const App = () => {
  const isLoggedIn = !!localStorage.getItem('access_token');  // 토큰 확인
  return (
    <Router>
    {isLoggedIn && <Navbar />}  {/* 로그인 상태일 때만 Navbar 보이게 */}
    <Routes>
      <Route path="/login" element={<Login />} />
      <Route path="/signup" element={<Signup />} />
      <Route
        path="/dashboard"
        element={isLoggedIn ? <Dashboard /> : <Navigate to="/login" />}
      />
      <Route
        path="/camera"
        element={isLoggedIn ? <Camera /> : <Navigate to="/login" />}
      />
      <Route path="*" element={<Navigate to={isLoggedIn ? "/dashboard" : "/login"} />} />
    </Routes>
  </Router>
);
};

export default App;
