import React, { useState } from 'react';
import { handleLogin } from '../api';

const Login = ({ setIsLoggedIn }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await handleLogin(username, password);

      if (response && response.status === 200) {
        localStorage.setItem('access_token', response.data.access);
        localStorage.setItem('refresh_token', response.data.refresh);
        alert('Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ! üéâ');
        setIsLoggedIn(true);
        window.location.href = '/navbar';  // Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ Ïãú ÎåÄÏãúÎ≥¥ÎìúÎ°ú Ïù¥Îèô
      } else {
        alert('Î°úÍ∑∏Ïù∏ Ïã§Ìå®. Îã§Ïãú ÏãúÎèÑÌïòÏÑ∏Ïöî.');
      }
    } catch (error) {
      if (error.response && error.response.status === 401) {
        alert('ÏïÑÏù¥Îîî ÎòêÎäî ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÌãÄÎ†∏ÏäµÎãàÎã§.');
      } else {
        alert('Î°úÍ∑∏Ïù∏ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      }
    }
  };

  return (
    <form onSubmit={onSubmit}>
      <input
        type="text"
        placeholder="ÏïÑÏù¥Îîî"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button type="submit">Î°úÍ∑∏Ïù∏</button>
    </form>
  );
};

export default Login;
