import React, { useEffect, useState } from 'react';
import axios from 'axios';

const getCSRFToken = () => {
    const cookies = document.cookie.split(';');
    for (let cookie of cookies) {
        const [key, value] = cookie.trim().split('=');
        if (key === 'csrftoken') return value;
    }
    return null;
};

// Axios instance with CSRF token interceptor
const api = axios.create({
    baseURL: 'http://localhost:8000/api/',
    withCredentials: true,
});

api.interceptors.request.use((config) => {
    const csrfToken = getCSRFToken();
    if (csrfToken) {
        config.headers['X-CSRFToken'] = csrfToken;
    }
    return config;
});

const Dashboard = () => {
    const [students, setStudents] = useState([]);
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    // Fetch student data
    const fetchStudents = async () => {
        try {
            setLoading(true);
            const response = await axios.get('http://localhost:8000/api/dashboard/', {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('access_token')}`, // 인증 토큰
                },
            });
            setStudents(response.data);
            setError(null);
        } catch (error) {
            console.error('Error fetching students:', error);
            setError('학생 데이터를 불러오는 데 실패했습니다.');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchStudents();
    }, []);

    // Handle file upload
    const handleFileUpload = async () => {
        if (!file) {
            alert('파일을 선택하세요.');
            return;
        }

        const formData = new FormData();
        formData.append('file', file);

        try {
            setLoading(true);
            const response = await axios.post('http://localhost:8000/api/upload-students/', formData, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('access_token')}`, // 인증 토큰
                    'Content-Type': 'multipart/form-data',
                },
            });
            console.log('파일 업로드 성공:', response.data);
            alert('파일 업로드 성공!');
            fetchStudents(); // 데이터 새로고침
        } catch (error) {
            console.error('파일 업로드 실패:', error);
            alert('파일 업로드 실패. 다시 시도해주세요.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <h1>Dashboard</h1>

            {/* File Upload Section */}
            <div style={{ marginBottom: '20px' }}>
                <input
                    type="file"
                    accept=".xlsx,.xls"
                    onChange={(e) => setFile(e.target.files[0])}
                />
                <button onClick={handleFileUpload} disabled={loading}>
                    {loading ? 'Uploading...' : 'Upload Excel'}
                </button>
            </div>

            {/* Error Message */}
            {error && <p style={{ color: 'red' }}>{error}</p>}

            {/* Student Data Table */}
            {loading ? (
                <p>Loading...</p>
            ) : (
                <table border="1" style={{ marginTop: '20px', width: '100%', textAlign: 'center' }}>
                    <thead>
                        <tr>
                            <th>학번</th>
                            <th>이름</th>
                            <th>출석현황</th>
                        </tr>
                    </thead>
                    <tbody>
                        {students.map((student) => (
                            <tr key={student.student_id}>
                                <td>{student.student_id}</td>
                                <td>{student.name}</td>
                                <td>{student.status || 'N/A'}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default Dashboard;
