import React, { useEffect, useState } from 'react';
import axios from 'axios';

const getCSRFToken = () => {
    const cookies = document.cookie.split(';');
    for (let cookie of cookies) {
        const [key, value] = cookie.trim().split('=');
        if (key === 'csrftoken') return value;
    }
    return null;
};

const Dashboard = () => {
    const [students, setStudents] = useState([]);
    const [file, setFile] = useState(null);

    // Fetch student data
    const fetchStudents = async () => {
        try {
            const response = await axios.get('http://localhost:8000/api/dashboard/');
            setStudents(response.data);
        } catch (error) {
            console.error('Error fetching students:', error);
        }
    };

    useEffect(() => {
        fetchStudents();
    }, []);

    // Handle file upload
    const handleFileUpload = async () => {
        if (!file) {
            alert('파일을 선택하세요.');
            return;
        }

        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await axios.post(
                'http://localhost:8000/api/upload-students/',
                formData,
                {
                    headers: {
                        'X-CSRFToken': getCSRFToken(), // CSRF 토큰 추가
                        'Content-Type': 'multipart/form-data',
                    },
                    withCredentials: true,
                }
            );
            console.log('파일 업로드 성공:', response.data);
            alert('파일 업로드 성공!');
            fetchStudents(); // 데이터 새로고침
        } catch (error) {
            console.error('파일 업로드 실패:', error);
            alert('파일 업로드 실패. 다시 시도해주세요.');
        }
    };

    return (
        <div>
            <h1>Dashboard</h1>

            {/* File Upload Section */}
            <div>
                <input
                    type="file"
                    accept=".xlsx,.xls"
                    onChange={(e) => setFile(e.target.files[0])}
                />
                <button onClick={handleFileUpload}>Upload Excel</button>
            </div>

            {/* Student Data Table */}
            <table border="1" style={{ marginTop: '20px', width: '100%', textAlign: 'center' }}>
                <thead>
                    <tr>
                        <th>학번</th>
                        <th>이름</th>
                    </tr>
                </thead>
                <tbody>
                    {students.map((student) => (
                        <tr key={student.student_id}>
                            <td>{student.student_id}</td>
                            <td>{student.name}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Dashboard;
